_ONE_DAY_IN_SECONDS = 60 * 60 * 24

class SpcServicer(spc_pb2_grpc.SpcServicer):
    def __init__(self):
        pass
    
    # i use json.loads and json.dumps to change the data into string
    # but in fact, userdict is a dict and output is a list contains several dicts like the former comment
    @staticmethod
    def GetResult(request, context):
        text = request.text
        userdict = json.loads(request.userdict)
        spc_output = module.function(text, userdict) # my function need two params
        result = spc_pb2.Output(result=json.dumps(spc_output)) 
        return result

def serve():
    server = grpc.server(futures.ThreadPoolExecutor(max_workers=10))
    spc_pb2_grpc.add_SpcServicer_to_server(
            SpcServicer(), server
            )
    server.add_insecure_port('[::]:50055')
    server.start()
    try:
        while True:
            time.sleep(_ONE_DAY_IN_SECONDS)
    except KeyboardInterrupt:
        server.stop(0)

if __name__ == '__main__':
    serve()


