using System;
using System.IO;
using OpenRA.FileSystem;

namespace OpenRA.Mods.Common.UtilityCommands
{
    class ListInstallShieldCabContentsCommand : IUtilityCommand
    {
        public string Name { get { return "--list-hdr"; } }

        public bool ValidateArguments(string[] args)
        {
            return args.Length == 2;
        }

        [Desc("DATA.HDR", "Lists the filenames contained within an InstallShield HDR index file")]
        public void Run(ModData modData, string[] args)
        {
            var filename = Path.GetFileName(args[1]);
            var path = Path.GetDirectoryName(args[1]);

            var fs = new OpenRA.FileSystem.FileSystem();
            fs.Mount(path, "parent");
            var package = new InstallShieldCABExtractor(fs, "parent|" + filename);

            foreach (var file in package.FileDescriptors)
            {
                Console.WriteLine("{0}:", file.Value.Filename);
                Console.WriteLine("\tOffset: {0}", file.Value.DataOffset);
                Console.WriteLine("\tLength: {0}", file.Value.NameOffset);
            }
        }
    }
}
