body:
Something like this should work:
```diff
diff --git a/OpenRA.Mods.D2k/Traits/World/BuildableTerrainLayer.cs b/OpenRA.Mods.D2k/Traits/World/BuildableTerrainLayer.cs
index da0267dcaa..909d7bd16a 100644
--- a/OpenRA.Mods.D2k/Traits/World/BuildableTerrainLayer.cs
+++ b/OpenRA.Mods.D2k/Traits/World/BuildableTerrainLayer.cs
@@ -11,6 +11,7 @@
 
 using System.Collections.Generic;
 using OpenRA.Graphics;
+using OpenRA.Mods.Common.Traits;
 using OpenRA.Traits;
 
 namespace OpenRA.Mods.D2k.Traits
@@ -34,6 +35,7 @@ public class BuildableTerrainLayer : IRenderOverlay, IWorldLoaded, ITickRender,
                readonly Map map;
                readonly CellLayer<int> strength;
 
+               BuildingInfluence bi;
                TerrainSpriteLayer render;
                Theater theater;
                bool disposed;
@@ -48,6 +50,7 @@ public BuildableTerrainLayer(Actor self, BuildableTerrainLayerInfo info)
                public void WorldLoaded(World w, WorldRenderer wr)
                {
                        theater = wr.Theater;
+                       bi = w.WorldActor.Trait<BuildingInfluence>();
                        render = new TerrainSpriteLayer(w, wr, theater.Sheet, BlendMode.Alpha, wr.Palette(info.Palette), wr.World.Type != WorldType.Editor);
                }
 
@@ -66,7 +69,7 @@ public void AddTile(CPos cell, TerrainTile tile)
 
                public void HitTile(CPos cell, int damage)
                {
-                       if (!strength.Contains(cell) || strength[cell] == 0)
+                       if (!strength.Contains(cell) || strength[cell] == 0 || bi.GetBuildingAt(cell) != null)
                                return;
 
                        strength[cell] = strength[cell] - damage;
```
