using System;
using OpenRA.Graphics;
using OpenRA.Traits;

namespace OpenRA.Mods.Common.Traits
{
    public class ActorDecorationInfo : ITraitInfo
    {
        public readonly bool ShowAllies = false;
        public readonly bool ShowEnemies = false;
        public readonly string Image = null;
        public readonly string Sequence = null;
        public readonly string Palette = null;

        public object Create(ActorInitializer init) { return new ActorDecoration(init.Self, this); }
    }

    public class ActorDecoration : IRenderOverlay
    {
        readonly Actor self;
        readonly ActorDecorationInfo info;
        readonly string image;
        readonly string animName;
        readonly string animPalette;
        readonly Animation anim;

        public ActorDecoration(Actor self, ActorDecorationInfo info)
        {
            this.self = self;
            this.info = info;

            image = info.Image ?? self.Info.Name;

            animName = info.Sequence;
            animPalette = info.Palette;

            anim = new Animation(self.World, image);
            anim.PlayRepeating(animName);
        }

        public void Tick(World world)
        {
            if (self.IsDead)
                return;

            anim.Tick();
        }

        public void RenderDecoration(WorldRenderer wr)
        {
            var pos = wr.ScreenPxPosition(self.CenterPosition);
            var bounds = self.Bounds;
            bounds.Offset(pos.X, pos.Y);

            var palette = wr.Palette(animPalette);
            var offset = (int)(4 / wr.Viewport.Zoom);
            var effectPos = wr.Position(new int2(bounds.Right - offset, bounds.Bottom - offset));
            var sr = new SpriteRenderable(anim.Image, effectPos, WVec.Zero, 0, palette, 1f / wr.Viewport.Zoom, true);
            sr.Render(wr);
        }

        public virtual bool ShouldRender(Actor self) { return true; }

        public void Render(WorldRenderer wr)
        {
            if (!self.IsInWorld)
                return;

            if (self.IsDead)
                return;

            if (wr.World.FogObscures(self))
                return;

            var allied = self.Owner.IsAlliedWith(self.World.RenderPlayer);

            if (!allied && !info.ShowEnemies)
                return;

            if (allied && !info.ShowAllies)
                return;

            if (ShouldRender(self))
                RenderDecoration(wr);
        }
    }
}
