using OpenRA.Mods.Common.Traits;
using OpenRA.Traits;

namespace OpenRA.Mods.RA.Traits
{
    public class DisguiseDecorationInfo : ActorDecorationInfo, Requires<DisguiseInfo>
    {
        [Desc("Require an active disguise to render this decoration?")]
        public readonly bool RequireDisguise = true;
    }

    public class DisguiseDecoration : ActorDecoration
    {
        readonly DisguiseDecorationInfo info;
        readonly Disguise disguise;

        public DisguiseDecoration(Actor self, DisguiseDecorationInfo info)
            : base(self, info)
        {
            this.info = info;
            disguise = self.Trait<Disguise>();
        }

        public override bool ShouldRender(Actor self)
        {
            return !info.RequireDisguise || disguise.Disguised;
        }
    }
}
