Benchmark script
```
'use strict';

var times = 1000;
var size = 1000000;
var array = [];
for (var i = 0; i < size; i++) {
  array[i] = Math.floor(Math.random() * size);
}

console.time('arrayMin');
for (var i = 0; i < times; i++) {
  arrayMin(array);
}

console.timeEnd('arrayMin');

console.time('arrayMax');
for (var i = 0; i < times; i++) {
  arrayMax(array);
}

console.timeEnd('arrayMax');

console.time('arrayMinPR');
for (var i = 0; i < times; i++) {
  arrayMinPR(array);
}

console.timeEnd('arrayMinPR');

console.time('arrayMaxPR');
for (var i = 0; i < times; i++) {
  arrayMaxPR(array);
}

console.timeEnd('arrayMaxPR');

// Methods

function arrayMin( array ) {

  if ( array.length === 0 ) {

    return Infinity;

  }

  var min = array[ 0 ];

  for ( var i = 1; i < array.length; ++ i ) {

    if ( array[ i ] < min ) {

      min = array[ i ];

    }

  }

  return min;

}

function arrayMax( array ) {

  if ( array.length === 0 ) {

    return - Infinity;

  }

  var max = array[ 0 ];

  for ( var i = 1; i < array.length; ++ i ) {

    if ( array[ i ] > max ) {

      max = array[ i ];

    }

  }

  return max;

}

function arrayMinPR( array ) {

  if ( array.length === 0 ) return Infinity;

  var min = array[ 0 ];

  for ( var i = 1, length = array.length; i < length; ++ i ) {

    if ( array[ i ] < min ) {

      min = array[ i ];

    }

  }

  return min;

}

function arrayMaxPR( array ) {

  if ( array.length === 0 ) return - Infinity;

  var max = array[ 0 ];

  for ( var i = 1, length = array.length; i < length; ++ i ) {

    if ( array[ i ] > max ) {

      max = array[ i ];

    }

  }

  return max;

}
