 		if ( visible ) {
 
-			if ( object.isLight ) {
+			if ( object.isSortingGroup ) {
+
+				groupOrder = object.renderOrder;
+
+			} else if ( object.isLight ) {
 
 				currentRenderState.pushLight( object );
 
